mapper.select:

SELECT * FROM (
    SELECT b.*, ROWNUM rnum FROM (
        SELECT * FROM board ORDER BY seq_board DESC
    ) b WHERE ROWNUM <= #{endRow}
) WHERE rnum > #{startRow}


SELECT * FROM (
    SELECT b.*, ROWNUM rnum FROM (
        SELECT * FROM board ORDER BY hit DESC
    ) b WHERE ROWNUM <= #{endRow}
) WHERE rnum > #{startRow}

mapper.update:
update board set 
image = #{image}, 
imageSubject = #{imageSubject}, 
imageContent = #{imageContent} 
where seq_board = #{seq_board}

update board set
imageSubject = #{imageSubject}, 
imageContent = #{imageContent} 
where seq_board = #{seq_board}
